    private Form mainForm;

    public HelloWorldMIDlet() {
        display = Display.getDisplay(this);
        mainForm = new Form("Hello World"); // Create a form
        // Add a string item to the form
        mainForm.append(new StringItem(null, "Hello, Java ME World!"));
    }

    public void startApp() {
        // Called when the MIDlet is started or resumed
        display.setCurrent(mainForm); // Make the form visible
    }

    public void pauseApp() {
        // Called when the MIDlet is paused
    }

    public void destroyApp(boolean unconditional) {
        // Called to shut down the MIDlet
        notifyDestroyed(); // Signal that we are destroyed
    }
}
